mapper.insert:
insert into java_details_record (java_details_id, account, state
  )
values (#{javaDetailsId,jdbcType=BIGINT}, #{account,jdbcType=VARCHAR}, #{state,jdbcType=BIT}
  )

mapper.select:
   select id, java_details_id, account, state
from java_details_record
where java_details_id = #{javaDetailsId} and account = #{account} and state = #{state}
