mapper.update:
update user
set alias = #{alias,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=BIGINT},
  email = #{email,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR}
where account = #{account}

update user
set
  img = #{path}
where account = #{account}

mapper.select:
select id, account, password, alias, phone, img, is_vip, state, email, description, register_time
from user
where account = #{0}
